func (p *Phase1) hash() []byte {
	if len(p.Challenge) == 0 {
		panic("challenge field missing")
	}
	sha := sha256.New()
	p.WriteTo(sha)
	sha.Write(p.Challenge)
	return sha.Sum(nil)
}
