// repeatUntilEnd for each wire, sets all the values starting from n > 0 to its predecessor.
// e.g. {{1, 2, 3}, {4, 5, 6}}.repeatUntilEnd(2) -> {{1, 2, 2}, {4, 5, 5}}
func (a WireAssignment) repeatUntilEnd(n int) {
	for i := range a {
		for j := n; j < len(a[i]); j++ {
			a[i][j] = a[i][j-1]
		}
	}
}
